Bash(powershell -NoProfile -File - <<'PSEOF'
$pdf = "C:\\Users\\E2 Training\\Downloads\\Sathvika Bachireddy DL copy.pdf"
$bytes = [System.IO.File]::ReadAllBytes\($pdf\)
$text = [System.Text.Encoding]::ASCII.GetString\($bytes\)
$patterns = @\('Producer', 'Creator', 'ModDate', 'CreationDate', 'Author', 'Adobe', 'Photoshop', 'GIMP', 'Canva', 'edit', 'modify'\)
foreach \($p in $patterns\) {
    $matches = [regex]::Matches\($text, "\(?i\)$p[^\\r\\n]{0,100}"\)
    foreach \($m in $matches\) {
        Write-Output $m.Value
    }
}
PSEOF)
